mapper.select:
SELECT ${params.xColumn}, ROUND(sum(${params.yColumn}),1) total FROM kechengjilu
 1=1 ${ew.sqlSegment}
group by ${params.xColumn}

select.if:
SELECT DATE_FORMAT(${params.xColumn},'%Y-%m-%d') ${params.xColumn}, sum(${params.yColumn}) total FROM kechengjilu
 1=1 ${ew.sqlSegment}
group by DATE_FORMAT(${params.xColumn},'%Y-%m-%d')

SELECT DATE_FORMAT(${params.xColumn},'%Y-%m') ${params.xColumn}, sum(${params.yColumn}) total FROM kechengjilu
 1=1 ${ew.sqlSegment}
group by DATE_FORMAT(${params.xColumn},'%Y-%m')

SELECT CONCAT(YEAR(${params.xColumn}), ' - ', ELT(QUARTER(${params.xColumn}), '第一季度', '第二季度', '第三季度', '第四季度')) as ${params.xColumn}, sum(${params.yColumn}) total FROM orders
 1=1 ${ew.sqlSegment}
group by CONCAT(YEAR(${params.xColumn}), ' - ', ELT(QUARTER(${params.xColumn}), '第一季度', '第二季度', '第三季度', '第四季度'))

SELECT DATE_FORMAT(${params.xColumn},'%Y') ${params.xColumn}, sum(${params.yColumn}) total FROM kechengjilu
 1=1 ${ew.sqlSegment}
group by DATE_FORMAT(${params.xColumn},'%Y')

mapper.select:
SELECT ${params.column} , count(1) total FROM kechengjilu
 1=1 ${ew.sqlSegment}
group by ${params.column}
